package package1;

public class DO_While_Osnove_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//DO-WHILE PETLJA - IZVRSICE SE MAKAR JEDNOM JER SE USLOV PROVERAVA NA KRAJU

		//primer 3.
				int i = 5;
				do {
					i++;
					System.out.println("Usao sam u petlju, trenutna vrednost i je " + i);
				} while (i < 10); {
					System.out.println("Kraj programa.");
				}
			}
		}
