public class While_Petlja_Osnove_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//WHILE PETLJA IZVRSAVA SE DOK GOD JE USLOV ZADOVOLJEN

				int a = 5;
				while (a > 3) {
					System.out.println("Usao sam u petlju, a vrednost promenljive a je " + a);
					// OVO JE BESKONACNA PETLJA
					a--; // SMANJUJEM VREDNOST PROMENLJIVE a ZA 1, DA PETLJA NE BUDE BESKONACNA.

					int b = 5;
					while (b < 10) {
						System.out.println("Usao sam u petlju, a vrednsot promenljive b je " + b);
						b++; //POVECAVAM VREDNOST PROMENLJIVE b ZA 1, DA PETLJA NE BUDE BESKONACNA.
					}}}}
