package package1;

public class While_Petlja_Zapis_Svako_Ulaska_U_Petlju {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		int n = 1;

		while (i < 15) {
			System.out.println("Usao sam u petlju " + n + ". put" + " i vrednost promenljive i je " + i + ".");
			i++;
			n++;
		}
	}
}


