package package1;

public class While_Petlja_Zapis_Svakog_Petog_Broja {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//NAPISI PROGRAM KOJI ISPISUJE SVAKI PETI BROJ IZMEÐU 10 I 50 I
		//ISPISUJE BROJ ULAZAKA U PETLJU
				
				int a = 10;
				int b = 50;
				int n = 1;
				
				while (a <= b) {
					System.out.println("Vrednost promenljive 10, usao sam u petlju " + n + ". put, a vrednost a je " + a + ".");
					a+=5; 
					n++;
					
				}
			}
		}
